sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {


    // Check if status is "Shipped"
    if (newValue.toLowerCase() === 'shipped') {
        // Corrected: Generate a random 10-digit number
        var trackingNumber = Math.floor(1000000000 + Math.random() * 9000000000);

        // Set the tracking number if not already set
        if (!g_form.getValue('tracking_number')) {
             g_form.setValue('tracking_number', 'TRX' + trackingNumber.toString());
        }
    }
}
